function showComponents(filter) {
    let buttonContainer = document.getElementById('button-container');
    buttonContainer.innerHTML = '';

    let components = [];

    if (filter === 'ranuras') {
        components = [
            { title: 'Ranura PCI', x: 120, y: 180, definition: 'Ranura PCI: Se utiliza para conectar tarjetas de expansión.', imageSrc: 'data:image/jpeg;base64,', features: ['Compatibilidad de Expansión: Permite la instalación de tarjetas de expansión como tarjetas gráficas, de sonido, de red y más.Ancho de Banda: Proporciona diferentes anchos de banda según la versión (PCI, PCI-X, PCIe) con velocidades de transferencia de datos variables.Versatilidad: Compatible con una amplia gama de dispositivos de expansión, lo que permite personalizar y actualizar el sistema según las necesidades.'] }
        ];
    } else if (filter === 'puertos') {
        components = [
            { title: 'Puerto USB', x: 200, y: 50, definition: 'Puerto USB: Conecta dispositivos periféricos.', imageSrc: 'data:image/jpeg;base64,', features: ['Tipos de Puertos: Incluye puertos USB 2.0, 3.0, 3.1, 3.2, y USB4, cada uno con diferentes velocidades de transferencia (desde 480 Mbps para USB 2.0 hasta 40 Gbps para USB4).Versatilidad: Permite conectar una amplia gama de dispositivos periféricos, como teclados, ratones, impresoras, discos duros externos y más.Compatibilidad: Soporta tanto dispositivos USB de tipo A (tradicional) como tipo C (reversible y más moderno).'] }
        ];
    } else if (filter === 'alimentacion') {
        components = [
            { title: 'Conector de Alimentación', x: 300, y: 120, definition: 'Conector de Alimentación: Suministra energía a la placa.', imageSrc: 'https://www.google.com/imgres?q=conector%20de%20alimentacion&imgurl=https%3A%2F%2Fhardzone.es%2Fapp%2Fuploads-hardzone.es%2F2022%2F10%2Fconector-atx-24-pines.jpg&imgrefurl=https%3A%2F%2Fhardzone.es%2Ftutoriales%2Fcomponentes%2Fconectores-fuente-alimentacion%2F&docid=NS06a_bc0tQqTM&tbnid=m0NxKcahJi0L5M&vet=12ahUKEwjP7IuAr5OIAxUJRzABHXX9Ou4QM3oECBsQAA..i&w=1000&h=563&hcb=2&ved=2ahUKEwjP7IuAr5OIAxUJRzABHXX9Ou4QM3oECBsQAA', features: ['24-pin ATX: Es el conector de alimentación principal que proporciona energía a la placa madre. Tiene 24 pines (anteriormente 20 pines) y se utiliza para alimentar la mayoría de los componentes.8-pin (4+4) EPS: Suministra energía adicional al procesador (CPU). Algunas placas madre tienen un conector de 8 pines, mientras que otras usan un conector de 4+4 pines que se puede conectar por separado.6-pin y 8-pin PCIe: Para suministrar energía adicional a las tarjetas gráficas. Los conectores de 6 pines y 8 pines proporcionan energía extra necesaria para las tarjetas gráficas de alto rendimiento.'] }
        ];
    } else if (filter === 'disipadores') {
        components = [
            { title: 'Disipador', x: 400, y: 150, definition: 'Disipador: Enfría los componentes para evitar sobrecalentamiento.', imageSrc: '.jpg', features: ['Disipadores de CPU: Son los más comunes y se montan directamente sobre el procesador. Pueden incluir una base de metal (generalmente cobre o aluminio) con aletas y, en algunos casos, un ventilador para mejorar la disipación del calor.Disipadores de Chipset: Enfriar el chipset de la placa madre, que también genera calor durante el funcionamiento. Estos disipadores suelen ser más pequeños que los de CPU y pueden estar pasivos (sin ventilador) o activos (con ventilador).'] }
        ];
    } else if (filter === 'chips') {
        components = [
            { title: 'Chipset', x: 350, y: 200, definition: 'Chipset: Controla la comunicación entre los componentes.', imageSrc: 'data:image/jpeg;base64,', features: ['Compatibilidad del Procesador (CPU): Determina qué tipos de procesadores son compatibles con la placa base. Esto incluye el tipo de socket y el soporte para diferentes generaciones y modelos de CPUs.Número de Zócalos para Memoria RAM: Indica la cantidad y tipo de memoria RAM que puede instalarse en la placa base. Los chipsets modernos suelen soportar DDR4 o DDR5, y pueden tener dos, cuatro o más ranuras para RAM.Soporte para Almacenamiento: Define los tipos de almacenamiento que puede gestionar, como HDD, SSD, y NVMe. También incluye el número de puertos SATA y la compatibilidad con tecnologías como RAID.'] }
        ];
    }

    for (let i = 0; i < components.length; i++) {
        let button = document.createElement('button');
        button.className = 'component-button';
        button.style.left = components[i].x + 'px';
        button.style.top = components[i].y + 'px';
        button.textContent = components[i].title;
        button.onclick = function() {
            showDefinition(components[i].title, components[i].definition, components[i].imageSrc, components[i].features);
        };
        buttonContainer.appendChild(button);
    }
}

function showDefinition(title, text, imageSrc, features) {
    let definition = document.getElementById('definition');
    let definitionTitle = document.getElementById('definition-title');
    let definitionText = document.getElementById('definition-text');
    let definitionImage = document.getElementById('definition-image');
    let definitionFeatures = document.getElementById('definition-features');
    let downloadButton = document.getElementById('download-button');

    definitionTitle.textContent = title;
    definitionText.textContent = text;
    definitionImage.src = imageSrc || ''; // Si no hay imagen, se muestra un espacio vacío
    definitionImage.style.display = imageSrc ? 'block' : 'none'; // Oculta la imagen si no hay src

    // Limpiar y agregar características
    definitionFeatures.innerHTML = '';
    if (features && features.length > 0) {
        features.forEach(feature => {
            let li = document.createElement('li');
            li.textContent = feature;
            definitionFeatures.appendChild(li);
        });
    }

    downloadButton.classList.remove('hidden');
    definition.classList.remove('hidden');

    downloadButton.onclick = function() {
        let canvas = document.createElement('canvas');
        let context = canvas.getContext('2d');
        canvas.width = 400;
        canvas.height = 200;

        context.fillStyle = 'white';
        context.fillRect(0, 0, canvas.width, canvas.height);
        context.font = '20px Arial';
        context.fillStyle = 'black';
        context.fillText(definitionTitle.textContent, 10, 30);
        context.fillText(definitionText.textContent, 10, 60);

        let link = document.createElement('a');
        link.download = 'definition.png';
        link.href = canvas.toDataURL('image/png');
        link.click();
    };

    function handleClickOutside(event) {
        if (!definition.contains(event.target)) {
            definition.classList.add('hidden');
            document.removeEventListener('click', handleClickOutside);
        }
    }

    setTimeout(() => {
        document.addEventListener('click', handleClickOutside);
    }, 0);
}
